async function i(){try{const e=Array.from(document.getElementsByClassName("btn_alarm type_on"));if(console.debug("[알림 해제 프로그램] 대상 :",e.length),!e.length||!confirm(`모든 채널의 알림을 해제하시겠습니까? (채널 갯수: ${e.length})`))return;for(let n=0;n<e.length;n++){e[n].click(),await new Promise(t=>setTimeout(t,300));const o=document.getElementsByClassName("btn_func");o.length>=2&&(o[1].click(),await new Promise(t=>setTimeout(t,200)),o[0].click()),await new Promise(t=>setTimeout(t,500))}}catch(e){console.error("에러 발생:",e)}}window.location.href==="https://m.notify.naver.com/channel"&&setTimeout(()=>{i()},1e3);
